            var xData = this.industrylist;
            // var lineData = [100, 100, 100, 100, 100, 100, 100]
            var lastYearData = this.devicenumlist;
            var thisYearData = this.enterprisenumlist;
            var timeLineData = [1];
            let legend = ['设备数', '企业数'];
            var background = '#fff'; //背景
            let textColor = '#000';
            let lineColor = 'rgba(255,255,255,0.2)';
            let colors = [
                {
                    borderColor: 'rgba(238，160,60,1)',
                    start: 'rgba(238,160,60,0.8)',
                    end: 'rgba(238,160,60,0.5)'
                },
                {
                    borderColor: 'rgba(35,169,242,1)',
                    start: 'rgba(35,169,242,0.5)',
                    end: 'rgba(35,169,242,0.8)'
                }
            ];
            let borderData = [];
            let scale = 2;
            // borderData = xData.map(item => {
            //     return 1;
            // });
            var option = {
                baseOption: {
                    title: {
                        show: true,
                        text: '设备数量企业数量行业分布图',
                        left: 'left'
                    },
                    backgroundColor: background,
                    timeline: {
                        show: false,
                        top: 0,
                        data: []
                    },
                    toolbox: {
                        show: true,
                        feature: {
                            saveAsImage: {}
                        }
                    },
                    legend: {
                        top: '5%',
                        right: '5%',
                        itemWidth: 20,
                        itemHeight: 8,
                        // itemGap: 343,
                        icon: 'horizontal',
                        textStyle: {
                            color: '#000',
                            fontSize: 16
                        },
                        data: legend
                    },
                    grid: [
                        {
                            show: false,
                            left: '5%',
                            top: '15%',
                            bottom: '8%',
                            containLabel: true,
                            width: '37%'
                        },
                        {
                            show: false,
                            left: '51%',
                            top: '15%',
                            bottom: '8%',
                            //    containLabel: true,
                            width: '0%'
                        },
                        {
                            show: false,
                            right: '2%',
                            top: '15%',
                            bottom: '8%',
                            containLabel: true,
                            width: '37%'
                        }
                    ],
                    xAxis: [
                        {
                            type: 'value',
                            inverse: true,
                            axisLine: {
                                show: false
                            },
                            // label: {
                            //     show: true
                            // },
                            axisTick: {
                                show: false
                            },
                            position: 'top',
                            axisLabel: {
                                show: true,
                                color: textColor
                            },
                            splitLine: {
                                show: true,
                                lineStyle: {
                                    color: lineColor
                                }
                            }
                        },
                        {
                            gridIndex: 1,
                            show: false
                        },
                        {
                            gridIndex: 2,
                            axisLine: {
                                show: false
                            },
                            axisTick: {
                                show: false
                            },
                            position: 'top',
                            axisLabel: {
                                show: true,
                                color: textColor
                            },
                            splitLine: {
                                show: true,
                                lineStyle: {
                                    color: lineColor
                                }
                            }
                        }
                    ],
                    yAxis: [
                        {
                            type: 'category',
                            inverse: true,
                            position: 'right',
                            axisLine: {
                                show: true,
                                lineStyle: {
                                    color: lineColor
                                }
                            },

                            axisTick: {
                                show: false
                            },
                            axisLabel: {
                                show: false
                            },
                            data: xData
                        },
                        {
                            gridIndex: 1,
                            type: 'category',
                            inverse: true,
                            position: 'left',
                            axisLine: {
                                show: false
                            },
                            axisTick: {
                                show: false
                            },
                            axisLabel: {
                                show: true,
                                padding: [30, 0, 0, 0],
                                textStyle: {
                                    color: '#000',
                                    fontSize: 14
                                },
                                align: 'center'
                            },
                            data: xData.map(function(value) {
                                return {
                                    value: value,
                                    textStyle: {
                                        align: 'center'
                                    }
                                };
                            })
                        },
                        {
                            gridIndex: 2,
                            type: 'category',
                            inverse: true,
                            position: 'left',
                            axisLine: {
                                show: true,
                                lineStyle: {
