import { fetchData, deleteData, addData, updateData, searchData } from '../../api/index';
export default {
    name: 'RoleList',
    data() {
        return {
            //rightlist:this.$refs.tree.getCheckedKeys().concat(this.$refs.tree.getHalfCheckedKeys()),
            data: [
                {
                    id: 1,
                    label: 'PC',
                    children: [
                        {
                            id: 2,
                            label: '用户地图',
                            children: [
                                {
                                    id: 3,
                                    label: '查询'
                                },
                                {
                                    id: 4,
                                    label: '导出'
                                }
                            ]
                        },
                        {
                            id: 5,
                            label: '产品档案'
                        },
                        {
                            id: 6,
                            label: '远程监控'
                        },
                        {
                            id: 7,
                            label: '服务手册'
                        },
                        {
                            id: 8,
                            label: '报警记录'
                        },
                        {
                            id: 9,
                            label: '交易分析'
                        },
                        {
                            id: 10,
                            label: '基础数据管理',
                            children: [
                                {
                                    id: 11,
                                    label: '数据字典'
                                },
                                {
                                    id: 12,
                                    label: '用户管理'
                                },
                                {
                                    id: 13,
                                    label: '角色管理'
                                },
                                {
                                    id: 14,
                                    label: '报警设置'
                                }
                            ]
                        }
                    ]
                }
            ],
            date: '',
            defaultProps: {
                children: 'children',
                label: 'label'
            },
            query: {
                pageIndex: 1,
                pageSize: 10
            },
            tableData: [],
            multipleSelection: [],
            // delList: [],
            editVisible: false,
            pageTotal: 0,
            form: {},
            idx: -1,
            isAdd: true
            // id: -1
        };
    },
    created() {
        this.getData();
    },
    methods: {
        // 获取所有数据
        getData() {
            fetchData('getAllRoleList').then(res => {
                console.log(res);
                this.tableData = res.data;
                this.pageTotal = this.tableData.length;
            });
        },
         // 触发搜索按钮
        handleSearch(value) {
            if (value !== '') {
                let query = {
                    keyword: value
                };
                searchData('getRole', query).then(res => {
                    this.tableData = res.data;                 
                    this.pageTotal = this.tableData.length;
                });
            } else {
                this.getData();
            }
            
        },
    
        // 删除操作
        handleDelete(index, row) {
            // 二次确认删除
            this.$confirm('确定要删除吗？', '提示', {
                type: 'warning'
            })
                .then(() => {
                    let query = {
                        ID: row.ID
                    };
                    deleteData('deleteDataDictionaryByID', query).then(res => {
                        this.getData();
                    });
                    this.$message.success('删除成功');
                })
                .catch(() => {});
        },
        // 多选操作
        handleSelectionChange(val) {
            this.multipleSelection = val;
        },
        //处理权限
        handleCheckChange(data, checked, indeterminate) {
            console.log(data, checked, indeterminate);
        },

        //添加确认
        Confirm() {
            if (this.form.role === '') {
                this.$message.error(`请输入必填项`);
            } else {
                if (this.isAdd) {
                    let query = {
                        role: this.form.role,
                        roleDescription: this.form.roleDescription,
                        right1: 1,
                        right2: 1,
                        right3: 1,
                        right4: 1,
                        right5: 1,
                        right6: 1,
                        right7: 1,
                        right8: 1,
                        right9: 1,
                        right10: 1,
                        right11: 1,
                        right12: 1,
                        right13: 1,
                        right14: 1
                    };

                    //console.log(query);
                    addData('/addRole', query)
                        .then(res => {
                            //console.log(res);
                            this.getData();
                        })
                        .catch(() => {});
                    this.editVisible = false;
                    this.$message.success('添加成功');
                } else {
                    let query = {
                        ID: this.form.ID,
                        role: this.form.role,
                        roleDescription: this.form.roleDescription,
                        right1: 1,
                        right2: 1,
                        right3: 1,
                        right4: 1,
                        right5: 1,
                        right6: 1,
                        right7: 1,
                        right8: 1,
                        right9: 1,
                        right10: 1,
                        right11: 1,
                        right12: 1,
                        right13: 1,
                        right14: 1
                    };
                    updateData('/updateDataDictionary', query)
                        .then(res => {
                            //console.log(res);
                            this.getData();
                        })
                        .catch(() => {});
                    this.editVisible = false;
                    this.$message.success('修改成功');
                }
                this.getData();
                this.form = {};
            }
        },
        //取消
        cancel() {
            this.editVisible = false;
            this.form = {};
        },
        // 编辑操作
        handleEdit(index, row) {
            this.idx = index;
            this.form = row;
            this.editVisible = true;
            this.isAdd = false;
        },
        // delAllSelection() {
        //     const length = this.multipleSelection.length;
        //     let str = '';
        //     this.delList = this.delList.concat(this.multipleSelection);
        //     for (let i = 0; i < length; i++) {
        //         str += this.multipleSelection[i].name + ' ';
        //     }
        //     this.$message.error(`删除了${str}`);
        //     this.multipleSelection = [];
        // },

        // 保存编辑
        // saveEdit() {
        //     this.editVisible = false;
        //     this.$message.success(`修改第 ${this.idx + 1} 行成功`);
        //     this.$set(this.tableData, this.idx, this.form);
        // },
        //新增角色
        addRole() {
            this.editVisible = true;
            this.isAdd = true;
        },
        // 分页导航
        handlePageChange(val) {
            // this.$set(this.query, 'pageIndex', val);
            // this.getData();
            this.query.pageIndex = val;
        }
    }
};
