export default {
    name: 'DeviceType',
    data() {
        return {
            keyword: '',
            tableData: [],
            editVisible: false,
            pageTotal: 0,
            pageIndex: 1,
            pageSize: 10,
            form: {},
            isAdd: true,
            idx: 1
        };
    },
    created() {
        this.getData();
    },

    methods: {
        //获取当前时间

        // 获取数据
        getData() {
            this.$axios
                .get('/dataSettings/DeviceType')
                .then(res => {
                    console.log(res.data);
                    this.tableData = res.data;
                    this.pageTotal = this.tableData.length;
                })
                .catch(err => {
                    console.log(err);
                });
        },
        // 触发搜索按钮
        handleSearch(value) {
            if (value !== '') {
                let query = {
                    keyword: value
                };
                this.$axios.post('/dataSettings/SearchDeviceType', query).then(res => {
                    if (res) {
                        this.tableData = res.data;
                        this.pageTotal = this.tableData.length;
                    }
                });
            } else {
                this.getData();
            }
        },
        // 删除操作
        handleDelete(index, row) {
            // 二次确认删除
            this.$confirm('确定要删除吗？', '提示', {
                type: 'warning'
            })
                .then(() => {
                    let query = {
                        id: this.tableData[index + this.pageSize * (this.pageIndex - 1)].id
                    };
                    this.$axios.post('/dataSettings/DeleteDeviceType', query).then(res => {
                        //console.log(res);
                        this.pageIndex = 1;
                        this.getData();
                        this.$message.success('删除成功');
                    });
                })
                .catch(err => {
                    console.log(err);
                });
        },
        // 多选操作
        handleSelectionChange(val) {
            this.multipleSelection = val;
        },

        //添加操作
        AddData() {
            this.editVisible = true;
            this.isAdd = true;
            this.form = {
                typename:''
                // is_deleted: 0
            };
        },

         //获取设备种类列表
        getdevicetypeelist() {
            let list = [];
            this.tableData.forEach(element => {
                list.push(element.typename);
            });
            return list;
        },
        //添加确认
        Confirm(formName) {
            // if (this.form.typename === '' || this.form.typename === '') {
            //     this.$message.error(`设备名称不能为空`);
            // } else if (this.form.typename.length > 128) {
            //     this.$message.error(`设备名称过长`);
            // } else {
            this.$refs[formName].validate(valid => {
                if (valid) {
                    if (this.isAdd) {
                        // this.query.industry_name=this.form.industry_name
                        let date = new Date();
                        this.form.created_time = date.getTime();
                        console.log(date);
                        this.$axios
                            .post('/dataSettings/AddDeviceType', this.form)
                            .then(res => {
                                console.log(res.data);
                                this.getData();
                            })
                            .catch(err => {
                                console.log(err);
                            });
                    } else {
                        this.form.id = this.idx;
                        delete this.form['created_time'];
                        // let date =new Date(this.form.created_time )
                        // this.form.created_time =  date.getTime();
                        this.$axios
                            .post('/dataSettings/updateDeviceType', this.form)
                            .then(res => {
                                console.log(res.data);
                                this.getData();
                                this.$message.success('修改成功');
                            })
                            .catch(err => {
                                console.log(err);
                            });
                    }
                    this.editVisible = false;
                    this.form = {};
                    // }
                } else {
                    console.log('error submit!!');
                    return false;
                }
            });
        },
        //取消
        Cancel() {
            this.editVisible = false;
            this.form = {};
            this.getData();
        },
        // 编辑操作
        handleEdit(index, row) {
            this.idx = this.tableData[index + (this.pageIndex - 1) * this.pageSize].id;
            this.form = row;
            this.editVisible = true;
            this.isAdd = false;
        },

        // 分页导航
        handlePageChange(val) {
            this.pageIndex = val;
        }
    }
};
