import { fetchData, deleteData, addData, updateData, searchData } from '../../api/index';
export default {
    name: 'DataDictionary',
    data() {
        return {
            options: [
                {
                    value: '客户行业',
                    label: '客户行业'
                },
                {
                    value: '企业行业',
                    label: '企业行业'
                },
                {
                    value: '故障类型',
                    label: '故障类型'
                },
                {
                    value: '故障现象',
                    label: '故障现象'
                },
                {
                    value: '设备种类',
                    label: '设备种类'
                },
                {
                    value: '设备名称',
                    label: '设备名称'
                },
                {
                    value: '型号描述',
                    label: '型号描述'
                }
            ],
            date: '',
            query: {
                pageIndex: 1,
                pageSize: 10
            },
            keyword: '',
            tableData: [],
          
            multipleSelection: [],
            delList: [],
            editVisible: false,
            pageTotal: 0,
            form: {},
            idx: -1,
            id: -1,
            isAdd: true
        };
    },
    created() {
        this.getData();
    },
   
    methods: {
      

        // 获取数据
        getData() {
            fetchData('getAllDataDictionary').then(res => {
                //console.log(res);
                this.tableData = res.data;
             
                this.pageTotal = this.tableData.length;
            });
        },
        // 触发搜索按钮
        handleSearch(value) {
            if (value !== '') {
                let query = {
                    keyword: value
                };
                searchData('getDataDictionary', query).then(res => {
                   // console.log('ok');
                    this.tableData = res.data;
                 
                    this.pageTotal = this.tableData.length;
                });
            } else {
                this.getData();
            }
            //this.tableData = this.filterBy(this.tableAll, value);
            //this.pageTotal = this.tableData.length;
        },
        // 删除操作
        handleDelete(index, row) {
            // 二次确认删除
            this.$confirm('确定要删除吗？', '提示', {
                type: 'warning'
            })
                .then(() => {
                    let query = {
                        ID: row.ID
                    };
                    deleteData('deleteDataDictionaryByID', query).then(res => {
                        //console.log(res);
                        this.getData();
                        //this.tableData = res.data;
                        // this.pageTotal = res.pageTotal || 50;
                    });
                    this.$message.success('删除成功');
                    // this.tableData.splice(index, 1);
                })
                .catch(() => {});
        },
        // 多选操作
        handleSelectionChange(val) {
            this.multipleSelection = val;
        },
        // delAllSelection() {
        //     const length = this.multipleSelection.length;
        //     let str = '';
        //     this.delList = this.delList.concat(this.multipleSelection);
        //     for (let i = 0; i < length; i++) {
        //         str += this.multipleSelection[i].name + ' ';
        //     }
        //     this.$message.error(`删除了${str}`);
        //     this.multipleSelection = [];
        // },
        //添加操作
        AddData() {
            this.editVisible = true;
            this.isAdd = true;
        },
        //添加确认
        Confirm() {
            if (this.form.classification === '' || this.keyname === '') {
                this.$message.error(`请输入必填项`);
            } else {
                if (this.isAdd) {
                    let query = {
                        classification: this.form.classification,
                        keyname: this.form.keyname,
                        description: this.form.description,
                        createuser: localStorage.getItem('ms_username')
                    };

                    //console.log(query);
                    addData('/addDataDictionary', query)
                        .then(res => {
                            //console.log(res);
                            this.getData();
                        })
                        .catch(() => {});
                    this.editVisible = false;
                    this.$message.success('添加成功');
                } else {
                    let query = {
                        ID: this.form.ID,
                        classification: this.form.classification,
                        keyname: this.form.keyname,
                        description: this.form.description,
                        createuser: localStorage.getItem('ms_username')
                    };
                    updateData('/updateDataDictionary', query)
                        .then(res => {
                            //console.log(res);
                            this.getData();
                        })
                        .catch(() => {});
                    this.editVisible = false;
                    this.$message.success('修改成功');
                }
                this.getData();
                this.form = {};
            }
        },
        //取消
        cancel() {
            this.editVisible = false;
            this.form = {};
        },
        // 编辑操作
        handleEdit(index, row) {
            this.idx = index;
            this.form = row;
            this.editVisible = true;
            this.isAdd = false;
        },

        //过滤
        // filterBy(tableData, value) {
        //     return tableData.filter(function(tableData) {
        //         return tableData.keyname.match(value);
        //     });
        // },

        // 分页导航
        handlePageChange(val) {
           // this.$set(this.query, 'pageIndex', val);
           // this.getData();
           this.query.pageIndex=val;
        }
    }
};
